:root {
	--font-family: 'Open Sans', sans-serif;
	--font-weight-normal: 400;
	--font-weight-bold: 600;
	--font-size-body: 12px;
	--font-size-label: 13px;
	--font-size-test-notice: 12px;
	--font-size-input: 14px;
	--font-size-header: 20px;
	--font-size-paragraph: 14px;
	--font-size-link: 12px;
	--font-size-error: 12px;
	--font-size-html-h1: 28px;
	--font-size-html-h2: 20px;
	--font-size-html-h3: 16px;
	--font-size-html-h4: 14px;
	--font-size-html-h5: 12px;
	--font-size-html-h6: 10px;
	--font-size-subheader: 14px;

	/* Colors */
	--color-background: #e8e8e8;
	--color-test-notice-text: #e6a23d;
	--color-test-notice-bg: #fefaf6;
	--color-test-notice-border: #f6dcb7;
	--color-card-bg: #ffffff;
	--color-card-border: #e0e0e0;
	--color-card-shadow: rgba(0, 0, 0, 0.08);
	--color-link: #2196F3;
	--color-header: #333333;
	--color-label: #757575;
	--color-input-border: none;
	--color-input-border-bottom: #9e9e9e;
	--color-input-border-hover: #616161;
	--color-input-text: #333333;
	--color-focus-border: #2196F3;
	--color-submit-btn-bg: #2196F3;
	--color-submit-btn-bg-disabled: #e0e0e0;
	--color-submit-btn-text: #ffffff;
	--color-submit-btn-text-disabled: #a0a0a0;
	--color-submit-btn-hover: #1976D2;
	--color-error: #ea1f30;
	--color-required: #ff6d5a;
	--color-clear-button-bg: #7e8186;
	--color-html-text: #555;
	--color-html-link: #2196F3;
	--color-header-subtext: #757575;

	/* Border Radii */
	--border-radius-card: 8px;
	--border-radius-input: 0px;
	--border-radius-button: 4px;
	--border-radius-clear-btn: 50%;
	--card-border-radius: 8px;

	/* Spacing */
	--padding-container-top: 32px;
	--padding-card: 32px;
	--padding-test-notice-vertical: 12px;
	--padding-test-notice-horizontal: 24px;
	--margin-bottom-card: 20px;
	--padding-form-input: 8px 2px;
	--card-padding: 32px;
	--card-margin-bottom: 20px;

	/* Dimensions */
	--container-width: 448px;
	--submit-btn-height: 48px;
	--checkbox-size: 18px;
	--input-border-bottom-width: 2px;

	/* Others */
	--box-shadow-card: 0px 2px 8px 0px var(--color-card-shadow);
	--opacity-placeholder: 0.5;
	--transition-speed: 0.2s;
}

